import React from 'react';
import './App.css';
import 'bulma/css/bulma.css'

import { SubmitForm } from './submit-form';
import { TodoList } from './todo-list';

import { BrowserRouter, Route, Switch } from 'react-router-dom';

import { faHippo } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export class ViewTask extends React.Component {

  state = {
    tasks: ['task 1', 'task 2', 'task 3']
  };

  handleSubmit = task => {
    this.setState({ tasks: [...this.state.tasks, task] });
  }

  handleDelete = (index) => {
    const newArr = [...this.state.tasks];
    newArr.splice(index, 1);
    this.setState({ tasks: newArr });
  }

  render() {
    return (
      <div className="app-wrapper">
        <BrowserRouter>
          <div class="container-header">
            <FontAwesomeIcon icon={faHippo}></FontAwesomeIcon>
            <p>ToDo App</p>
          </div>

        <div>
          <SubmitForm onFormSubmit={this.handleSubmit}></SubmitForm>
        </div>
        <div className='container-list' >
          <TodoList tasks={this.state.tasks} onDelete={this.handleDelete}></TodoList>
        </div>
        </BrowserRouter>
      </div>
    );
  }
}     

export default ViewTask;
